from mpi4py import MPIcomm = MPI.COMM_WORLDrank = comm.Get_rank()size=comm.sizeif rank == 0:    print("ROOT aquí!")    for x in range(size):        if x != 0:           comm.ssend(x,dest=x)               #Root recive desde ranks diferentes a cero(0)    data1=comm.recv(source=MPI.ANY_SOURCE,tag=1)        for y in range(size):             if y!=0:               data=comm.recv(source=y)            print ("Proceso número: ",rank,"... valor recibido ", data)        else:            print ("Proceso número: ",rank,"... valor recibido ", data1)else:        #Acá se recibe el valor enviado por el root    data=comm.recv(source=0)    print ("Proceso número: ",rank,"... valor recibido ", data, "desde 0")    #datax=comm.recv(source=0,tag=2)    #print ("Proceso número: ",rank,"... valor recibido ", datax, "desde 0")        #Se reenvia el cuadrado hacia root    #Si se usa ssend entra en deathlock    comm.send(data*data,dest=0)         #Acá se envia el valor hacia el root desde los esclavos    #Se empieza a tratar al root como esclavo    comm.send(0,0,tag=1)        